mapper.insert:
INSERT INTO /*+ insert_TB_CO_M_LATC_CLF_STD */ TB_CO_M_LATC_CLF_STD
(
      LATC_LCLC           /* 품목대분류코드       */
    , LATC_MCLC           /* 품목중분류코드       */
    , LATC_SCLC           /* 품목소분류코드       */
    , LATC_LCLC_NM        /* 품목대분류명         */
    , LATC_MCLC_NM        /* 품목중분류명         */
    , LATC_SCLC_NM        /* 품목소분류명         */
    , USE_YN              /* 사용여부             */
    , RMK_CMTM            /* 비고                 */
    , CHG_DSC             /* 변동구분(변경/신규)  */
)
VALUES
(
      #{LATC_LCLC}
    , #{LATC_MCLC}
    , #{LATC_SCLC}
    , #{LATC_LCLC_NM}
    , #{LATC_MCLC_NM}
    , #{LATC_SCLC_NM}
    , #{USE_YN}
    , #{RMK_CMTM}
    , #{CHG_DSC}
)

mapper.delete:
DELETE /*+ delete_TB_CO_M_LATC_CLF_STD */ FROM TB_CO_M_LATC_CLF_STD
 WHERE 1=1 
   AND LATC_LCLC           = #{LATC_LCLC}
   AND LATC_MCLC           = #{LATC_MCLC}
   AND LATC_SCLC           = #{LATC_SCLC}
