mapper.insert:
INSERT INTO /*+ insert_TB_CO_M_USR_VOICE */ TB_CO_M_USR_VOICE
(
      CORP_C              /* 회사코드             */
    , VOICE_SQNO          /* 사용자목소리일련번호 */
    , BSN_DSC             /* 업무구분코드         */
    , TIT                 /* 제목                 */
    , CNTN                /* 내용                 */
    , APD_FILE_AMN_NO     /* 첨부파일관리번호     */
    , INQCN               /* 조회수               */
    , PRET_VOICE_SQNO     /* 부모사용자목소리일련 */
    , DEL_YN              /* 삭제여부             */
    , ETC_DSC1            /* 기타구분1            */
    , FSRG_DTM            /* 최초등록일시         */
    , FSRG_ID             /* 최초등록자ID         */
    , LSCHG_DTM           /* 최종변경일시         */
    , LSCHG_ID            /* 최종변경자ID         */
)
VALUES
(
      #{CORP_C}
    , #{VOICE_SQNO}
    , #{BSN_DSC}
    , #{TIT}
    , #{CNTN}
    , #{APD_FILE_AMN_NO}
    , 0
    , ISNULL(DBO.SF_GET_NUMERIC(#{PRET_VOICE_SQNO}), 0) 
    , 'N'
    , #{ETC_DSC1}
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
)

mapper.delete:
DELETE /*+ delete_TB_CO_M_USR_VOICE */ FROM TB_CO_M_USR_VOICE
 WHERE 1=1 
   AND CORP_C              = #{CORP_C}
   AND VOICE_SQNO          = #{VOICE_SQNO}
