mapper.insert:
INSERT INTO /*+ insert_TB_SE_M_PRGR */ TB_SE_M_PRGR
(
      CORP_C              /* 회사코드             */
    , BZPL_C              /* 사업장코드           */
    , PRGR_C              /* 가격군코드           */
    , PRGR_NM             /* 가격군명             */
    , RMK_CNTN            /* 비고내용             */
    , DEL_YN              /* 삭제여부             */
    , FSRG_DTM            /* 최초등록일시         */
    , FSRG_ID             /* 최초등록자ID         */
    , LSCHG_DTM           /* 최종변경일시         */
    , LSCHG_ID            /* 최종변경자ID         */
)
VALUES
(
      #{CORP_C}
    , #{BZPL_C}
    , RIGHT('0' + CAST((
                        SELECT ISNULL(MAX(PRGR_C),0)+1
                          FROM TB_SE_M_PRGR
                         WHERE 1 = 1
                           AND CORP_C = #{CORP_C}
                           AND BZPL_C = #{BZPL_C}
                        )AS NVARCHAR), 2)
    , #{PRGR_NM}
    , #{RMK_CNTN}
    , #{DEL_YN}
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
)

mapper.delete:
DELETE /*+ delete_TB_SE_M_PRGR */ FROM TB_SE_M_PRGR
 WHERE 1=1 
   AND CORP_C              = #{CORP_C}         
   AND BZPL_C              = #{BZPL_C}
   AND PRGR_C              = #{PRGR_C}
