mapper.insert:
INSERT INTO /*+ insert_TB_SE_M_TRPL_DVYAA */ TB_SE_M_TRPL_DVYAA
(
      CORP_C              /* 회사코드             */
    , BZPL_C              /* 사업장코드           */
    , TRPL_C              /* 거래처코드           */
    , REG_SQNO            /* 등록일련번호         */
    , DVYAA_ZIP           /* 배송지우편번호       */
    , DVYAA_ADR1          /* 배송지주소1          */
    , DVYAA_ADR2          /* 배송지주소2          */
    , REP_DVYAA_YN        /* 대표배송지여부       */
    , RMK_CNTN            /* 비고내용             */
    , DEL_YN              /* 삭제여부             */
    , FSRG_DTM            /* 최초등록일시         */
    , FSRG_ID             /* 최초등록자ID         */
    , LSCHG_DTM           /* 최종변경일시         */
    , LSCHG_ID            /* 최종변경자ID         */
)
VALUES
(
      #{CORP_C}
    , #{BZPL_C}
    , #{TRPL_C}
    , (SELECT ISNULL(MAX(REG_SQNO),0)+1 
         FROM TB_SE_M_TRPL_DVYAA
        WHERE 1 = 1
          AND CORP_C = #{CORP_C}
          AND BZPL_C = #{BZPL_C}
          AND TRPL_C = #{TRPL_C}
      )
    , #{DVYAA_ZIP}
    , #{DVYAA_ADR1}
    , #{DVYAA_ADR2}
    , #{REP_DVYAA_YN}
    , #{RMK_CNTN}
    , #{DEL_YN}
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
)

mapper.delete:
DELETE /*+ delete_TB_SE_M_TRPL_DVYAA */ FROM TB_SE_M_TRPL_DVYAA
 WHERE 1=1 
   AND CORP_C              = #{CORP_C}         
   AND BZPL_C              = #{BZPL_C}
   AND TRPL_C              = #{TRPL_C}
   AND REG_SQNO            = #{REG_SQNO}
