mapper.insert:
INSERT INTO /*+ insert_TB_SY_D_USR_ROLE */ TB_SY_D_USR_ROLE
(
      CORP_C              /* 회사코드             */
    , USR_ID              /* 사용자아이디         */
    , EMP_ROL_DSC         /* 직원역할구분         */
    , FSRG_DTM            /* 최초등록일시         */
    , FSRG_ID             /* 최초등록자ID         */
    , LSCHG_DTM           /* 최종변경일시         */
    , LSCHG_ID            /* 최종변경자ID         */
)
VALUES
(
      #{CORP_C}
    , #{USR_ID}
    , #{EMP_ROL_DSC}
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
)

mapper.delete:
DELETE /*+ delete_TB_SY_D_USR_ROLE */ FROM TB_SY_D_USR_ROLE
 WHERE 1=1 
   AND CORP_C              = #{CORP_C}
   AND EMP_ROL_DSC         = #{EMP_ROL_DSC}
   AND USR_ID              = #{USR_ID}
