mapper.insert:
INSERT INTO /*+ insert_TB_SY_L_INTF_CRT */ TB_SY_L_INTF_CRT
(
      CORP_C              /* 회사코드             */
    , INTF_ID             /* 인터페이스ID         */
    , INTF_CSE_SQNO       /* 연계추적일련번호     */
    , RQR_RSP_DSC         /* 요청응답구분코드     */
    , TR_SQNO             /* 거래일련번호         */
    , CRT_RQR_DT          /* 생성요청일자         */
    , CRT_RQR_TM          /* 생성요청시간         */
    , ERR_MSG             /* 에러메세지           */
    , ERR_RSP_C           /* 에러응답코드         */
    , INTF_DATA           /* 연계자료             */
    , ETC_DSC1            /* 기타구분1            */
    , FSRG_DTM            /* 최초등록일시         */
    , FSRG_ID             /* 최초등록자ID         */
    , LSCHG_DTM           /* 최종변경일시         */
    , LSCHG_ID            /* 최종변경자ID         */
)
VALUES
(
      #{CORP_C}
    , #{INTF_ID}
    , #{INTF_CSE_SQNO}
    , #{RQR_RSP_DSC}
    , #{TR_SQNO}
    , #{CRT_RQR_DT}
    , #{CRT_RQR_TM}
    , #{ERR_MSG}
    , #{ERR_RSP_C}
    , #{INTF_DATA}
    , #{ETC_DSC1}
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
)

mapper.delete:
DELETE /*+ delete_TB_SY_L_INTF_CRT */ FROM TB_SY_L_INTF_CRT
 WHERE 1=1 
   AND CORP_C              = #{CORP_C}
   AND INTF_CSE_SQNO       = #{INTF_CSE_SQNO}
   AND INTF_ID             = #{INTF_ID}
   AND RQR_RSP_DSC         = #{RQR_RSP_DSC}
