UPDATE /*+ delete_TB_CO_D_BSN_CTC */ FROM TB_CO_D_BSN_CTC
   SET DEL_YN              = 'Y'
     , LSCHG_DTM           = GETDATE()
     , LSCHG_ID            = #{GUSRID}
 WHERE 1=1 
   AND CORP_C              = #{CORP_C}
   AND BSN_CTC_SQNO        = #{BSN_CTC_SQNO}
   AND RCST_ID             = #{RCST_ID}
