mapper.insert:
INSERT INTO /*+ insert_TB_CO_M_APDFL */ TB_CO_M_APDFL
(
      CORP_C              /* 회사코드             */
    , APD_FILE_AMN_NO     /* 첨부파일관리번호     */
    , APD_FILE_SQNO       /* 첨부파일일련번호     */
    , APD_FILE_DSC        /* 첨부파일구분코드     */
    , STOG_PATH           /* 저장경로             */
    , STOG_FILE_NM        /* 저장파일명           */
    , OTXT_FILE_NM        /* 원본파일명           */
    , XTN_NM              /* 확장자명             */
    , FILE_SZE            /* 파일크기             */
    , ETC_DSC1            /* 기타구분1            */
    , FSRG_DTM            /* 최초등록일시         */
    , FSRG_ID             /* 최초등록자ID         */
    , LSCHG_DTM           /* 최종변경일시         */
    , LSCHG_ID            /* 최종변경자ID         */
)
VALUES
(
      #{CORP_C}
    , #{APD_FILE_AMN_NO}
    , #{APD_FILE_SQNO}
    , #{APD_FILE_DSC}
    , #{STOG_PATH}
    , #{STOG_FILE_NM}
    , #{OTXT_FILE_NM}
    , #{XTN_NM}
    , #{FILE_SZE}
    , #{ETC_DSC1}
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
)

mapper.delete:
DELETE /*+ delete_TB_CO_M_APDFL */ FROM TB_CO_M_APDFL
 WHERE 1=1 
   AND APD_FILE_SQNO       = #{APD_FILE_SQNO}
   AND CORP_C              = #{CORP_C}
