mapper.insert:
INSERT INTO /*+ insert_TB_SE_L_GDS_UPR */ TB_SE_L_GDS_UPR
(
      CORP_C                                            /* 회사코드             */
    , BZPL_C                                            /* 사업장코드           */
    , GDS_C                                             /* 물품코드             */
    , HST_SQNO                                          /* 이력일련번호         */
    , APL_BAS_DT                                        /* 적용기준일자         */
    , WHSL_SL_UPR                                       /* 도매매출단가         */
    , RTL_SL_UPR                                        /* 소매매출단가         */
    , ARCY_SPY_UPR                                      /* 대리점공급단가       */    
    , RMK_CNTN                                          /* 비고내용             */
    , DEL_YN                                            /* 삭제여부             */
    , FSRG_DTM                                          /* 최초등록일시         */
    , FSRG_ID                                           /* 최초등록자ID         */
    , LSCHG_DTM                                         /* 최종변경일시         */
    , LSCHG_ID                                          /* 최종변경자ID         */
)
VALUES
(
      #{CORP_C}
    , #{BZPL_C}
    , #{GDS_C}
    , (
       SELECT ISNULL(MAX(HST_SQNO),0)+1
         FROM TB_SE_L_GDS_UPR
        WHERE CORP_C = #{CORP_C}
          AND BZPL_C = #{BZPL_C}
          AND GDS_C  = #{GDS_C}
      )
    , #{APL_BAS_DT}
    , DBO.SF_GET_FLOAT(#{WHSL_SL_UPR})
    , DBO.SF_GET_FLOAT(#{RTL_SL_UPR})
    , DBO.SF_GET_FLOAT(#{ARCY_SPY_UPR})
    , #{RMK_CNTN}
    , 'N'
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
)

mapper.delete:
DELETE /*+ delete_TB_SE_L_GDS_UPR */ FROM TB_SE_L_GDS_UPR
 WHERE 1=1 
   AND BZPL_C              = #{BZPL_C}
   AND CORP_C              = #{CORP_C}
   AND GDS_C               = #{GDS_C}
   AND HST_SQNO            = #{HST_SQNO}
