mapper.insert:
INSERT INTO /*+ insert_TB_SE_M_DVY_CUS */ TB_SE_M_DVY_CUS
(
      CORP_C              /* 회사코드             */
    , BZPL_C              /* 사업장코드           */
    , DVY_CUS_REG_DT      /* 배송고객등록일자     */
    , DVY_CUS_REG_SQNO    /* 배송고객등록일련번호 */
    , ODR_CUS_NM          /* 주문고객명           */
    , ODR_CUS_TEL         /* 주문고객연락처       */
    , RCP_CUS_NM          /* 수령고객명           */
    , RCP_CUS_TEL         /* 수령고객연락처       */
    , RCP_CNTN            /* 수령내용             */
    , DVY_RQR_CNTN        /* 배송요청내용         */
    , RMK_CNTN            /* 비고내용             */
    , DEL_YN              /* 삭제여부             */
    , ETC_DSC1            /* 기타구분1            */
    , ETC_DSC2            /* 기타구분2            */
    , ETC_DSC3            /* 기타구분3            */
    , FSRG_DTM            /* 최초등록일시         */
    , FSRG_ID             /* 최초등록자ID         */
    , LSCHG_DTM           /* 최종변경일시         */
    , LSCHG_ID            /* 최종변경자ID         */
)
VALUES
(
      #{CORP_C}
    , #{BZPL_C}
    , #{DVY_CUS_REG_DT}
    , #{DVY_CUS_REG_SQNO}
    , #{ODR_CUS_NM}
    , REPLACE(#{ODR_CUS_TEL}, '-', '')
    , #{RCP_CUS_NM}
    , REPLACE(#{RCP_CUS_TEL}, '-', '')
    , #{RCP_CNTN}
    , #{DVY_RQR_CNTN}
    , #{RMK_CNTN}
    , 'N'
    , #{ETC_DSC1}
    , #{ETC_DSC2}
    , #{ETC_DSC3}
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
)

mapper.delete:
DELETE /*+ delete_TB_SE_M_DVY_CUS */ FROM TB_SE_M_DVY_CUS
 WHERE 1=1 
   AND CORP_C              = #{CORP_C}         
   AND BZPL_C              = #{BZPL_C}
   AND DVY_CUS_REG_DT      = #{DVY_CUS_REG_DT}
   AND DVY_CUS_REG_SQNO    = #{DVY_CUS_REG_SQNO}
