mapper.insert:
INSERT INTO /*+ insert_TB_SE_M_ONL_OPR */ TB_SE_M_ONL_OPR
(
      CORP_C              /* 회사코드             */
    , BZPL_C              /* 사업장코드           */
    , RG_DT               /* 등록일자             */
    , RG_SQNO             /* 등록일련번호         */
    , RG_DSQNO            /* 등록상세일련번호     */
    , TRPL_C              /* 거래처코드           */
    , OPR_DT              /* 주문일자             */
    , OPR_NO              /* 주문번호             */
    , TRPL_GDS_C          /* 거래처물품코드       */
    , TRPL_GDS_NM         /* 거래처물품명         */
    , GDS_C               /* 물품코드             */
    , OPR_QT              /* 주문수량             */
    , OPR_UPR             /* 주문단가             */
    , OPR_AM              /* 주문금액             */
    , DVYAA_ZIP           /* 배송지우편번호       */
    , DVYAA_ADR1          /* 배송지주소1          */
    , DVYAA_ADR2          /* 배송지주소2          */
    , DVY_CUS_REG_DT      /* 배송고객등록일자     */
    , DVY_CUS_REG_SQNO    /* 배송고객등록일련번호 */
    , RVO_DT              /* 수주일자             */
    , RVO_SQNO            /* 수주일련번호         */
    , RMK_CNTN            /* 비고내용             */
    , DEL_YN              /* 삭제여부             */
    , ETC_DSC1            /* 기타구분1            */
    , ETC_DSC2            /* 기타구분2            */
    , ETC_DSC3            /* 기타구분3            */
    , FSRG_DTM            /* 최초등록일시         */
    , FSRG_ID             /* 최초등록자ID         */
    , LSCHG_DTM           /* 최종변경일시         */
    , LSCHG_ID            /* 최종변경자ID         */
)
VALUES
(
      #{CORP_C}
    , #{BZPL_C}
    , #{RG_DT}
    , #{RG_SQNO}
    , (
       SELECT ISNULL(MAX(RG_DSQNO),0)+1
         FROM TB_SE_M_ONL_OPR
        WHERE 1 = 1
          AND CORP_C  = #{CORP_C}
          AND BZPL_C  = #{BZPL_C}
          AND RG_DT   = #{RG_DT}
          AND RG_SQNO = #{RG_SQNO} 
      )
    , #{TRPL_C}
    , REPLACE(#{OPR_DT},'-','')
    , #{OPR_NO}
    , #{TRPL_GDS_C}
    , #{TRPL_GDS_NM}
    , #{GDS_C}
    , DBO.SF_GET_FLOAT(#{OPR_QT})
    , DBO.SF_GET_FLOAT(#{OPR_UPR})
    , DBO.SF_GET_NUMERIC(#{OPR_AM})
    , REPLACE(#{DVYAA_ZIP}, '-', '')
    , #{DVYAA_ADR1}
    , #{DVYAA_ADR2}
    , #{DVY_CUS_REG_DT}
    , DBO.SF_GET_NUMERIC(#{DVY_CUS_REG_SQNO})
    , #{RVO_DT}
    , DBO.SF_GET_NUMERIC(#{RVO_SQNO})
    , #{RMK_CNTN}
    , 'N'
    , #{ETC_DSC1}
    , #{ETC_DSC2}
    , #{ETC_DSC3}
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
)

mapper.delete:
DELETE /*+ delete_TB_SE_M_ONL_OPR */ FROM TB_SE_M_ONL_OPR
 WHERE 1=1 
   AND BZPL_C              = #{BZPL_C}
   AND CORP_C              = #{CORP_C}
   AND RG_DSQNO            = #{RG_DSQNO}
   AND RG_DT               = #{RG_DT}
   AND RG_SQNO             = #{RG_SQNO}
