mapper.insert:
INSERT INTO /*+ insert_TB_SE_M_PRGR_GDS */ TB_SE_M_PRGR_GDS
(
      CORP_C              /* 회사코드             */
    , BZPL_C              /* 사업장코드           */
    , PRGR_C              /* 가격군코드           */
    , GDS_C               /* 물품코드             */
    , APL_BAS_UPR         /* 적용기준단가         */
    , APL_SL_UPR          /* 적용매출단가         */
    , APL_RT              /* 적용율               */
    , APL_UPR             /* 적용단가             */
    , APL_BAS_DT          /* 적용기준일자         */
    , RMK_CNTN            /* 비고내용             */
    , DEL_YN              /* 삭제여부             */
    , FSRG_DTM            /* 최초등록일시         */
    , FSRG_ID             /* 최초등록자ID         */
    , LSCHG_DTM           /* 최종변경일시         */
    , LSCHG_ID            /* 최종변경자ID         */
)
VALUES
(
      #{CORP_C}
    , #{BZPL_C}
    , #{PRGR_C}
    , #{GDS_C}
    , DBO.SF_GET_FLOAT(#{APL_BAS_UPR})
    , DBO.SF_GET_FLOAT(#{APL_SL_UPR})
    , DBO.SF_GET_FLOAT(#{APL_RT})
    , DBO.SF_GET_FLOAT(#{APL_UPR})
    , #{APL_BAS_DT}
    , #{RMK_CNTN}
    , #{DEL_YN}
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
)

mapper.delete:
DELETE /*+ delete_TB_SE_M_PRGR_GDS */ FROM TB_SE_M_PRGR_GDS
 WHERE 1=1 
   AND CORP_C              = #{CORP_C}         
   AND BZPL_C              = #{BZPL_C}
   AND PRGR_C              = #{PRGR_C}
   AND GDS_C               = #{GDS_C}           
   AND APL_BAS_DT          = #{APL_BAS_DT}
