mapper.insert:
INSERT INTO /*+ insert_TB_SY_L_ERR_LOG */ TB_SY_L_ERR_LOG
(
      CORP_C              /* 회사코드             */
    , ERR_SQNO            /* 에러일련번호         */
    , LOG_DSC_NM          /* 로그구분명           */
    , ERR_DTM             /* 에러발생일시         */
    , USR_ID              /* 사용자아이디         */
    , CONN_IP             /* 접속IP               */
    , PGM_ID              /* 프로그램ID           */
    , CLASS_NM            /* 클래스명             */
    , METHOD_NM           /* 메쏘드명             */
    , SRC_FILE_NM         /* 소스파일명           */
    , LINE_NO             /* 소스라인NO           */
    , ERR_MSG             /* 에러메세지           */
    , FSRG_DTM            /* 최초등록일시         */
    , FSRG_ID             /* 최초등록자ID         */
    , LSCHG_DTM           /* 최종변경일시         */
    , LSCHG_ID            /* 최종변경자ID         */
)
SELECT
      #{CORP_C}
    , ISNULL(MAX(ERR_SQNO), 0) + 1
    , #{LOG_DSC_NM}
    , GETDATE()
    , #{GUSRID}
    , #{CONN_IP}
    , #{PGM_ID}
    , #{CLASS_NM}
    , #{METHOD_NM}
    , #{SRC_FILE_NM}
    , #{LINE_NO}
    , #{ERR_MSG}
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
 FROM TB_SY_L_ERR_LOG
WHERE 1=1
  AND CORP_C = #{CORP_C}

mapper.delete:
DELETE /*+ delete_TB_SY_L_ERR_LOG */ FROM TB_SY_L_ERR_LOG
 WHERE 1=1 
   AND CORP_C              = #{CORP_C}
   AND ERR_SQNO            = #{ERR_SQNO}
