mapper.insert:
INSERT INTO /*+ insert_TB_SY_M_PGID */ TB_SY_M_PGID
(
      CORP_C              /* 회사코드             */
    , PGM_ID              /* 프로그램ID           */
    , BSN_DSC             /* 업무구분코드         */
    , PGM_NM              /* 프로그램명           */
    , PGM_WK_DSC          /* 프로그램작업구분코드 */
    , PGM_EXPL            /* 프로그램설명         */
    , PGM_EXE_PATH        /* 프로그램실행경로     */
    , USE_YN              /* 사용여부             */
    , DEL_YN              /* 삭제여부             */
    , HLP_FILE_YN         /* 도움말파일여부       */
    , FSRG_DTM            /* 최초등록일시         */
    , FSRG_ID             /* 최초등록자ID         */
    , LSCHG_DTM           /* 최종변경일시         */
    , LSCHG_ID            /* 최종변경자ID         */
)
VALUES
(
      #{CORP_C}
    , #{PGM_ID}
    , #{BSN_DSC}
    , #{PGM_NM}
    , #{PGM_WK_DSC}
    , #{PGM_EXPL}
    , #{PGM_EXE_PATH}
    , #{USE_YN}
    , #{DEL_YN}
    , #{HLP_FILE_YN}
    , GETDATE()
    , #{GUSRID}
    , GETDATE()
    , #{GUSRID}
)

mapper.delete:
DELETE /*+ delete_TB_SY_M_PGID */ FROM TB_SY_M_PGID
 WHERE 1=1 
   AND CORP_C              = #{CORP_C}
   AND PGM_ID              = #{PGM_ID}
